import difflib

def get_diff(doc_before :str, doc_after :str):

    doc_before = doc_before.split()
    doc_after = doc_after.split()

    output_list = [li for li in difflib.ndiff(doc_before, doc_after) if li[0] != ' ']

    print(output_list)

